        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/core/LeverageVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// Price oracle interface
interface IMETHPriceOracle {
    function getPrice() external view returns (uint256);
}

// SeniorPool interface
interface ISeniorPool {
    function borrow(uint256 positionId, uint256 amount) external;

    function repay(
        uint256 positionId,
        uint256 amount
    ) external returns (uint256 principal, uint256 interest);

    function getOutstandingDebt(uint256 positionId) external view returns (uint256);

    function getAccruedInterest(uint256 positionId) external view returns (uint256);
}

// FluxionIntegration interface
interface IFluxionIntegration {
    function swapMETHToUSDC(uint256 mETHAmount) external returns (uint256);
}

/**
 * @title LeverageVault
 * @notice Core vault for managing leveraged RWA token purchases using mETH collateral
 * @dev Handles collateral custody, position management, yield harvesting, and settlement waterfall
 *
 * Flow:
 * 1. User deposits mETH as collateral (150% LTV)
 * 2. Vault borrows USDC from SeniorPool
 * 3. Backend purchases RWA tokens, transfers to vault
 * 4. Daily harvest: mETH yield → USDC → Interest payment
 * 5. Settlement: Waterfall distribution (Senior → Interest → User yield)
 * 6. Liquidation: If health factor < 110%
 */
contract LeverageVault is Ownable, ReentrancyGuard {
    // External contracts
    IERC20 public mETH;
    IERC20 public usdc;
    address public seniorPool;
    address public fluxionIntegration;
    address public yieldVault;
    IMETHPriceOracle public priceOracle;

    // Position tracking
    struct Position {
        address user; // Investor address
        uint256 mETHCollateral; // mETH deposited (18 decimals)
        uint256 usdcBorrowed; // USDC borrowed from SeniorPool (6 decimals)
        address rwaToken; // RWA token address
        uint256 rwaTokenAmount; // RWA tokens held (18 decimals)
        string assetId; // Asset ID reference
        uint256 createdAt; // Position creation timestamp
        uint256 lastHarvestTime; // Last yield harvest timestamp
        uint256 totalInterestPaid; // Cumulative interest paid
        bool active; // Position status
    }

    mapping(uint256 => Position) public positions;
    uint256 public nextPositionId;

    // Health factor parameters
    uint256 public constant LIQUIDATION_THRESHOLD = 11000; // 110% (basis points)
    uint256 public constant INITIAL_LTV = 15000; // 150% (basis points)
    uint256 public constant BASIS_POINTS = 10000;

    // Events
    event PositionCreated(
        uint256 indexed positionId,
        address indexed user,
        uint256 mETHCollateral,
        uint256 usdcBorrowed,
        address rwaToken,
        uint256 rwaTokenAmount
    );
    event YieldHarvested(
        uint256 indexed positionId,
        uint256 mETHSwapped,
        uint256 usdcReceived,
        uint256 interestPaid
    );
    event PositionLiquidated(
        uint256 indexed positionId,
        uint256 mETHSold,
        uint256 usdcRecovered,
        uint256 shortfall
    );
    event SettlementProcessed(
        uint256 indexed positionId,
        uint256 seniorRepayment,
        uint256 interestRepayment,
        uint256 userYield
    );
    event CollateralAdded(uint256 indexed positionId, uint256 amount);

    /**
     * @notice Initialize Leverage Vault
     * @param _mETH mETH token address
     * @param _usdc USDC token address
     * @param _seniorPool SeniorPool contract address
     * @param _fluxionIntegration FluxionIntegration contract address
     * @param _priceOracle Price oracle address
     */
    constructor(
        address _mETH,
        address _usdc,
        address _seniorPool,
        address _fluxionIntegration,
        address _priceOracle
    ) Ownable(msg.sender) {
        mETH = IERC20(_mETH);
        usdc = IERC20(_usdc);
        seniorPool = _seniorPool;
        fluxionIntegration = _fluxionIntegration;
        priceOracle = IMETHPriceOracle(_priceOracle);
        nextPositionId = 1;
    }

    /**
     * @notice Set YieldVault address (one-time)
     * @param _yieldVault YieldVault contract address
     */
    function setYieldVault(address _yieldVault) external onlyOwner {
        require(yieldVault == address(0), "YieldVault already set");
        require(_yieldVault != address(0), "Invalid address");
        yieldVault = _yieldVault;
    }

    /**
     * @notice Create leverage position
     * @param user Investor address
     * @param mETHAmount mETH collateral amount (18 decimals)
     * @param usdcToBorrow USDC to borrow (6 decimals)
     * @param rwaToken RWA token address
     * @param rwaTokenAmount RWA token amount (18 decimals)
     * @param assetId Asset ID reference
     * @return positionId Created position ID
     */
    function createPosition(
        address user,
        uint256 mETHAmount,
        uint256 usdcToBorrow,
        address rwaToken,
        uint256 rwaTokenAmount,
        string memory assetId
    ) external onlyOwner nonReentrant returns (uint256 positionId) {
        require(user != address(0), "Invalid user");
        require(mETHAmount > 0, "mETH amount must be > 0");
        require(usdcToBorrow > 0, "USDC amount must be > 0");
        require(rwaToken != address(0), "Invalid RWA token");

        // Verify LTV (collateral must be >= 150% of loan)
        uint256 collateralValueUSD = _getMETHValueUSD(mETHAmount);
        uint256 requiredCollateral = (usdcToBorrow * INITIAL_LTV) / BASIS_POINTS;
        require(
            collateralValueUSD >= requiredCollateral,
            "Insufficient collateral (150% LTV required)"
        );

        // Transfer mETH from user to vault
        require(
            mETH.transferFrom(user, address(this), mETHAmount),
            "mETH transfer failed"
        );

        // Borrow USDC from SeniorPool
        ISeniorPool(seniorPool).borrow(nextPositionId, usdcToBorrow);

        // Create position
        positionId = nextPositionId++;
        positions[positionId] = Position({
            user: user,
            mETHCollateral: mETHAmount,
            usdcBorrowed: usdcToBorrow,
            rwaToken: rwaToken,
            rwaTokenAmount: rwaTokenAmount,
            assetId: assetId,
            createdAt: block.timestamp,
            lastHarvestTime: block.timestamp,
            totalInterestPaid: 0,
            active: true
        });

        emit PositionCreated(
            positionId,
            user,
            mETHAmount,
            usdcToBorrow,
            rwaToken,
            rwaTokenAmount
        );
    }

    /**
     * @notice Harvest mETH yield and pay interest
     * @param positionId Position ID
     * @return mETHSwapped Amount of mETH converted to USDC
     * @return usdcReceived Amount of USDC received from swap
     * @return interestPaid Amount of interest paid
     */
    function harvestYield(
        uint256 positionId
    )
        external
        onlyOwner
        nonReentrant
        returns (uint256 mETHSwapped, uint256 usdcReceived, uint256 interestPaid)
    {
        Position storage position = positions[positionId];
        require(position.active, "Position not active");

        // Get outstanding interest from SeniorPool
        uint256 outstandingInterest = ISeniorPool(seniorPool).getAccruedInterest(
            positionId
        );
        require(outstandingInterest > 0, "No interest to pay");

        // Calculate mETH appreciation since last harvest
        uint256 mETHPrice = priceOracle.getPrice();
        uint256 currentValue = _getMETHValueUSD(position.mETHCollateral);

        // Determine mETH to swap (enough to cover interest)
        mETHSwapped = _calculateMETHToSwap(outstandingInterest, mETHPrice);
        require(mETHSwapped <= position.mETHCollateral, "Insufficient collateral");

        // Approve and swap mETH for USDC
        mETH.approve(fluxionIntegration, mETHSwapped);
        usdcReceived = IFluxionIntegration(fluxionIntegration).swapMETHToUSDC(
            mETHSwapped
        );

        // Pay interest to SeniorPool
        interestPaid = usdcReceived > outstandingInterest
            ? outstandingInterest
            : usdcReceived;
        usdc.approve(seniorPool, interestPaid);
        ISeniorPool(seniorPool).repay(positionId, interestPaid);

        // Update position
        position.mETHCollateral -= mETHSwapped;
        position.totalInterestPaid += interestPaid;
        position.lastHarvestTime = block.timestamp;

        emit YieldHarvested(positionId, mETHSwapped, usdcReceived, interestPaid);
    }

    /**
     * @notice Liquidate position if health factor < 110%
     * @param positionId Position ID
     * @return usdcRecovered USDC recovered from liquidation
     * @return shortfall USDC shortfall (if any)
     */
    function liquidatePosition(
        uint256 positionId
    ) external onlyOwner nonReentrant returns (uint256 usdcRecovered, uint256 shortfall) {
        Position storage position = positions[positionId];
        require(position.active, "Position not active");

        // Verify liquidation is necessary
        uint256 healthFactor = getHealthFactor(positionId);
        require(healthFactor < LIQUIDATION_THRESHOLD, "Position is healthy");

        // Swap all mETH collateral for USDC
        uint256 mETHAmount = position.mETHCollateral;
        mETH.approve(fluxionIntegration, mETHAmount);
        usdcRecovered = IFluxionIntegration(fluxionIntegration).swapMETHToUSDC(
            mETHAmount
        );

        // Get outstanding debt
        uint256 outstandingDebt = ISeniorPool(seniorPool).getOutstandingDebt(
            positionId
        );

        // Repay as much as possible
        uint256 repaymentAmount = usdcRecovered > outstandingDebt
            ? outstandingDebt
            : usdcRecovered;
        usdc.approve(seniorPool, repaymentAmount);
        ISeniorPool(seniorPool).repay(positionId, repaymentAmount);

        // Calculate shortfall
        shortfall = outstandingDebt > usdcRecovered
            ? outstandingDebt - usdcRecovered
            : 0;

        // Mark position as inactive
        position.active = false;
        position.mETHCollateral = 0;

        emit PositionLiquidated(positionId, mETHAmount, usdcRecovered, shortfall);
    }

    /**
     * @notice Process settlement waterfall when RWA asset settles
     * @param positionId Position ID
     * @param settlementUSDC Total USDC from asset settlement
     * @return seniorRepayment Amount repaid to SeniorPool
     * @return interestRepayment Amount paid for interest
     * @return userYield Amount distributed to user
     */
    function processSettlement(
        uint256 positionId,
        uint256 settlementUSDC
    )
        external
        onlyOwner
        nonReentrant
        returns (uint256 seniorRepayment, uint256 interestRepayment, uint256 userYield)
    {
        Position storage position = positions[positionId];
        require(position.active, "Position not active");

        // Get outstanding debt from SeniorPool
        uint256 totalDebt = ISeniorPool(seniorPool).getOutstandingDebt(positionId);

        // Waterfall distribution:
        // 1. Senior Pool principal
        // 2. Accrued interest
        // 3. Remaining USDC to user
        if (settlementUSDC >= totalDebt) {
            // Full repayment + user yield
            usdc.approve(seniorPool, totalDebt);
            (seniorRepayment, interestRepayment) = ISeniorPool(seniorPool).repay(
                positionId,
                totalDebt
            );
            userYield = settlementUSDC - totalDebt;

            // Transfer user yield
            if (userYield > 0) {
                require(
                    usdc.transfer(position.user, userYield),
                    "User yield transfer failed"
                );
            }
        } else {
            // Partial repayment (prioritize principal, then interest)
            usdc.approve(seniorPool, settlementUSDC);
            (seniorRepayment, interestRepayment) = ISeniorPool(seniorPool).repay(
                positionId,
                settlementUSDC
            );
            userYield = 0;
        }

        // Return remaining mETH collateral to user
        if (position.mETHCollateral > 0) {
            require(
                mETH.transfer(position.user, position.mETHCollateral),
                "mETH return failed"
            );
        }

        // Mark position as inactive
        position.active = false;

        emit SettlementProcessed(
            positionId,
            seniorRepayment,
            interestRepayment,
            userYield
        );
    }

    /**
     * @notice Add collateral to position
     * @param positionId Position ID
     * @param mETHAmount Additional mETH collateral
     */
    function addCollateral(
        uint256 positionId,
        uint256 mETHAmount
    ) external nonReentrant {
        Position storage position = positions[positionId];
        require(position.active, "Position not active");
        require(msg.sender == position.user, "Not position owner");
        require(mETHAmount > 0, "Amount must be > 0");

        // Transfer mETH from user
        require(
            mETH.transferFrom(msg.sender, address(this), mETHAmount),
            "mETH transfer failed"
        );

        position.mETHCollateral += mETHAmount;

        emit CollateralAdded(positionId, mETHAmount);
    }

    /**
     * @notice Get health factor for position
     * @param positionId Position ID
     * @return Health factor (basis points, e.g., 15000 = 150%)
     */
    function getHealthFactor(uint256 positionId) public view returns (uint256) {
        Position memory position = positions[positionId];
        if (!position.active) return 0;

        uint256 collateralValueUSD = _getMETHValueUSD(position.mETHCollateral);
        uint256 outstandingDebt = ISeniorPool(seniorPool).getOutstandingDebt(
            positionId
        );

        if (outstandingDebt == 0) return type(uint256).max;

        // Health factor = (collateralValue / debt) * BASIS_POINTS
        return (collateralValueUSD * BASIS_POINTS) / outstandingDebt;
    }

    /**
     * @notice Get position details
     * @param positionId Position ID
     * @return Position struct
     */
    function getPosition(uint256 positionId) external view returns (Position memory) {
        return positions[positionId];
    }

    /**
     * @notice Calculate mETH to swap for target USDC amount
     * @param targetUSDC Target USDC amount (6 decimals)
     * @param mETHPrice Current mETH price (18 decimals)
     * @return mETH amount needed (18 decimals)
     */
    function _calculateMETHToSwap(
        uint256 targetUSDC,
        uint256 mETHPrice
    ) internal pure returns (uint256) {
        // mETH = (targetUSDC * 1e18 * 1e12) / mETHPrice
        // 1e12 converts USDC (6 decimals) to 18 decimals
        return (targetUSDC * 1e30) / mETHPrice;
    }

    /**
     * @notice Get USD value of mETH amount
     * @param mETHAmount mETH amount (18 decimals)
     * @return USD value (6 decimals for USDC)
     */
    function _getMETHValueUSD(uint256 mETHAmount) internal view returns (uint256) {
        uint256 price = priceOracle.getPrice(); // 18 decimals
        // Convert to USDC 6 decimals: (mETH * price) / 1e30
        return (mETHAmount * price) / 1e30;
    }
}

contracts/core/SeniorPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title SeniorPool
 * @notice USDC lending pool for leverage positions at 5% APR
 * @dev Provides USDC loans to LeverageVault for investor leverage purchases
 *
 * Features:
 * - USDC deposits from liquidity providers
 * - Loans with 5% APR interest accrual
 * - Liquidity reserve requirements (20-30%)
 * - Debt ceiling per position ($100k max)
 * - Interest compounding for demo mode
 */
contract SeniorPool is Ownable, ReentrancyGuard {
    IERC20 public usdc;
    address public leverageVault;

    // Pool parameters
    uint256 public constant APR = 500; // 5% APR (basis points)
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant DEBT_CEILING = 100_000 * 1e6; // $100k per position (USDC 6 decimals)
    uint256 public constant RESERVE_RATIO = 2000; // 20% reserve requirement (basis points)

    // Pool state
    uint256 public totalLiquidity; // Total USDC deposited
    uint256 public totalBorrowed; // Total USDC currently borrowed
    uint256 public totalInterestEarned; // Cumulative interest earned

    // Loan tracking
    struct Loan {
        uint256 principal; // Original loan amount
        uint256 interestAccrued; // Accumulated interest
        uint256 lastUpdateTime; // Last interest calculation timestamp
        bool active; // Loan status
    }

    mapping(uint256 => Loan) public loans; // positionId => Loan

    // Demo mode for accelerated time
    bool public demoMode;
    uint256 public timeMultiplier; // e.g., 360 for 360x acceleration

    // Events
    event LiquidityDeposited(address indexed depositor, uint256 amount);
    event LiquidityWithdrawn(address indexed withdrawer, uint256 amount);
    event LoanIssued(uint256 indexed positionId, uint256 amount);
    event LoanRepaid(uint256 indexed positionId, uint256 principal, uint256 interest);
    event InterestAccrued(uint256 indexed positionId, uint256 interest);
    event DemoModeUpdated(bool enabled, uint256 multiplier);

    modifier onlyLeverageVault() {
        require(msg.sender == leverageVault, "Only LeverageVault");
        _;
    }

    /**
     * @notice Initialize Senior Pool
     * @param _usdc USDC token address
     */
    constructor(address _usdc) Ownable(msg.sender) {
        usdc = IERC20(_usdc);
        demoMode = false;
        timeMultiplier = 1;
    }

    /**
     * @notice Set LeverageVault address (one-time)
     * @param _leverageVault LeverageVault contract address
     */
    function setLeverageVault(address _leverageVault) external onlyOwner {
        require(leverageVault == address(0), "LeverageVault already set");
        require(_leverageVault != address(0), "Invalid address");
        leverageVault = _leverageVault;
    }

    /**
     * @notice Enable/disable demo mode for time acceleration
     * @param _enabled Enable demo mode
     * @param _multiplier Time multiplier (e.g., 360 for 360x)
     */
    function setDemoMode(bool _enabled, uint256 _multiplier) external onlyOwner {
        require(_multiplier > 0, "Multiplier must be > 0");
        demoMode = _enabled;
        timeMultiplier = _multiplier;
        emit DemoModeUpdated(_enabled, _multiplier);
    }

    /**
     * @notice Deposit USDC liquidity into pool
     * @param amount Amount of USDC to deposit (6 decimals)
     */
    function depositLiquidity(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be > 0");

        require(
            usdc.transferFrom(msg.sender, address(this), amount),
            "USDC transfer failed"
        );

        totalLiquidity += amount;
        emit LiquidityDeposited(msg.sender, amount);
    }

    /**
     * @notice Withdraw USDC liquidity from pool
     * @param amount Amount of USDC to withdraw (6 decimals)
     */
    function withdrawLiquidity(uint256 amount) external onlyOwner nonReentrant {
        require(amount > 0, "Amount must be > 0");
        require(amount <= getAvailableLiquidity(), "Insufficient available liquidity");

        totalLiquidity -= amount;
        require(usdc.transfer(msg.sender, amount), "USDC transfer failed");

        emit LiquidityWithdrawn(msg.sender, amount);
    }

    /**
     * @notice Borrow USDC for leverage position
     * @param positionId Unique position identifier
     * @param amount Amount to borrow (6 decimals)
     */
    function borrow(uint256 positionId, uint256 amount) external onlyLeverageVault nonReentrant {
        require(amount > 0, "Amount must be > 0");
        require(amount <= DEBT_CEILING, "Exceeds debt ceiling");
        require(!loans[positionId].active, "Loan already exists");
        require(amount <= getAvailableLiquidity(), "Insufficient liquidity");

        // Create loan
        loans[positionId] = Loan({
            principal: amount,
            interestAccrued: 0,
            lastUpdateTime: block.timestamp,
            active: true
        });

        totalBorrowed += amount;

        // Transfer USDC to LeverageVault
        require(usdc.transfer(leverageVault, amount), "USDC transfer failed");

        emit LoanIssued(positionId, amount);
    }

    /**
     * @notice Repay loan (principal + interest)
     * @param positionId Position identifier
     * @param amount Amount to repay (6 decimals)
     * @return principal Principal repaid
     * @return interest Interest repaid
     */
    function repay(
        uint256 positionId,
        uint256 amount
    ) external onlyLeverageVault nonReentrant returns (uint256 principal, uint256 interest) {
        require(loans[positionId].active, "Loan not active");

        // Update interest before repayment
        _accrueInterest(positionId);

        Loan storage loan = loans[positionId];
        uint256 totalOwed = loan.principal + loan.interestAccrued;
        require(amount <= totalOwed, "Amount exceeds debt");

        // Calculate principal and interest portions
        if (amount <= loan.interestAccrued) {
            // Repaying only interest
            interest = amount;
            principal = 0;
            loan.interestAccrued -= interest;
        } else {
            // Repaying interest + principal
            interest = loan.interestAccrued;
            principal = amount - interest;
            loan.interestAccrued = 0;
            loan.principal -= principal;
        }

        // Update pool state
        totalBorrowed -= principal;
        totalInterestEarned += interest;
        totalLiquidity += interest; // Interest stays in pool

        // Transfer USDC from LeverageVault
        require(
            usdc.transferFrom(leverageVault, address(this), amount),
            "USDC transfer failed"
        );

        // Close loan if fully repaid
        if (loan.principal == 0) {
            loan.active = false;
        }

        emit LoanRepaid(positionId, principal, interest);
    }

    /**
     * @notice Get outstanding debt for position (principal + accrued interest)
     * @param positionId Position identifier
     * @return Outstanding debt amount
     */
    function getOutstandingDebt(uint256 positionId) external view returns (uint256) {
        if (!loans[positionId].active) return 0;

        Loan memory loan = loans[positionId];
        uint256 pendingInterest = _calculateInterest(positionId);
        return loan.principal + loan.interestAccrued + pendingInterest;
    }

    /**
     * @notice Get accrued interest for position
     * @param positionId Position identifier
     * @return Accrued interest amount
     */
    function getAccruedInterest(uint256 positionId) external view returns (uint256) {
        if (!loans[positionId].active) return 0;

        Loan memory loan = loans[positionId];
        uint256 pendingInterest = _calculateInterest(positionId);
        return loan.interestAccrued + pendingInterest;
    }

    /**
     * @notice Get available liquidity for lending
     * @return Available USDC amount
     */
    function getAvailableLiquidity() public view returns (uint256) {
        // Ensure reserve requirement is met
        uint256 requiredReserve = (totalBorrowed * RESERVE_RATIO) / BASIS_POINTS;
        uint256 available = totalLiquidity > totalBorrowed
            ? totalLiquidity - totalBorrowed
            : 0;

        return available > requiredReserve ? available - requiredReserve : 0;
    }

    /**
     * @notice Accrue interest for a loan
     * @param positionId Position identifier
     */
    function accrueInterest(uint256 positionId) external {
        require(loans[positionId].active, "Loan not active");
        _accrueInterest(positionId);
    }

    /**
     * @notice Internal: Accrue interest for a loan
     * @param positionId Position identifier
     */
    function _accrueInterest(uint256 positionId) internal {
        Loan storage loan = loans[positionId];
        uint256 interest = _calculateInterest(positionId);

        if (interest > 0) {
            loan.interestAccrued += interest;
            loan.lastUpdateTime = block.timestamp;
            emit InterestAccrued(positionId, interest);
        }
    }

    /**
     * @notice Internal: Calculate pending interest
     * @param positionId Position identifier
     * @return Pending interest amount
     */
    function _calculateInterest(uint256 positionId) internal view returns (uint256) {
        Loan memory loan = loans[positionId];
        if (!loan.active || loan.principal == 0) return 0;

        uint256 timeElapsed = block.timestamp - loan.lastUpdateTime;

        // Apply time multiplier for demo mode
        if (demoMode) {
            timeElapsed = timeElapsed * timeMultiplier;
        }

        // Interest = (principal * APR * timeElapsed) / (BASIS_POINTS * SECONDS_PER_YEAR)
        // APR is in basis points (500 = 5%)
        uint256 interest = (loan.principal * APR * timeElapsed) /
            (BASIS_POINTS * 365 days);

        return interest;
    }

    /**
     * @notice Get pool statistics
     * @return Total liquidity, total borrowed, available liquidity, total interest earned
     */
    function getPoolStats()
        external
        view
        returns (uint256, uint256, uint256, uint256)
    {
        return (
            totalLiquidity,
            totalBorrowed,
            getAvailableLiquidity(),
            totalInterestEarned
        );
    }
}

contracts/integrations/FluxionIntegratio.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// DEX interface
interface IFluxionDEX {
    function swapMETHForUSDC(
        uint256 mETHAmount,
        uint256 minUSDCOut
    ) external returns (uint256 usdcOut);

    function getQuote(uint256 mETHAmount) external view returns (uint256);
}

// Price oracle interface
interface IMETHPriceOracle {
    function getPrice() external view returns (uint256);
}

/**
 * @title FluxionIntegration
 * @notice Wrapper for Fluxion DEX integration with slippage protection
 * @dev Handles mETH → USDC swaps for yield harvesting with safety checks
 *
 * Features:
 * - Slippage protection (max 3%)
 * - Price validation against oracle
 * - Swap statistics tracking
 * - Emergency pause functionality
 */
contract FluxionIntegration is Ownable, ReentrancyGuard {

    IERC20 public mETH;
    IERC20 public usdc;
    IFluxionDEX public dex;
    IMETHPriceOracle public priceOracle;

    // Slippage parameters
    uint256 public constant MAX_SLIPPAGE = 300; // 3% max slippage (basis points)
    uint256 public constant BASIS_POINTS = 10000;

    // Swap statistics
    uint256 public totalSwapsExecuted;
    uint256 public totalMETHSwapped;
    uint256 public totalUSDCReceived;

    // Emergency controls
    bool public paused;

    // Events
    event SwapExecuted(
        uint256 indexed timestamp,
        uint256 mETHAmount,
        uint256 usdcReceived,
        uint256 effectiveRate
    );
    event SlippageExceeded(uint256 expected, uint256 actual, uint256 slippage);
    event PausedUpdated(bool paused);

    modifier whenNotPaused() {
        require(!paused, "Contract is paused");
        _;
    }

    /**
     * @notice Initialize Fluxion Integration
     * @param _mETH mETH token address
     * @param _usdc USDC token address
     * @param _dex Fluxion DEX address
     * @param _priceOracle Price oracle address (can be mETH token itself)
     */
    constructor(
        address _mETH,
        address _usdc,
        address _dex,
        address _priceOracle
    ) Ownable(msg.sender) {
        mETH = IERC20(_mETH);
        usdc = IERC20(_usdc);
        dex = IFluxionDEX(_dex);
        priceOracle = IMETHPriceOracle(_priceOracle);
        paused = false;
    }

    /**
     * @notice Pause/unpause swaps (emergency)
     * @param _paused New pause state
     */
    function setPaused(bool _paused) external onlyOwner {
        paused = _paused;
        emit PausedUpdated(_paused);
    }

    /**
     * @notice Update DEX address
     * @param _dex New DEX address
     */
    function setDEX(address _dex) external onlyOwner {
        require(_dex != address(0), "Invalid address");
        dex = IFluxionDEX(_dex);
    }

    /**
     * @notice Update price oracle address
     * @param _oracle New oracle address
     */
    function setPriceOracle(address _oracle) external onlyOwner {
        require(_oracle != address(0), "Invalid address");
        priceOracle = IMETHPriceOracle(_oracle);
    }

    /**
     * @notice Swap mETH for USDC with slippage protection
     * @param mETHAmount Amount of mETH to swap (18 decimals)
     * @return usdcReceived Amount of USDC received (6 decimals)
     */
    function swapMETHToUSDC(
        uint256 mETHAmount
    ) external nonReentrant whenNotPaused returns (uint256 usdcReceived) {
        require(mETHAmount > 0, "Amount must be > 0");

        // Get quote from DEX
        uint256 expectedUSDC = dex.getQuote(mETHAmount);
        require(expectedUSDC > 0, "Invalid quote");

        // Validate quote against oracle price
        _validatePrice(mETHAmount, expectedUSDC);

        // Calculate minimum output with slippage tolerance
        uint256 minUSDCOut = (expectedUSDC * (BASIS_POINTS - MAX_SLIPPAGE)) /
            BASIS_POINTS;

        // Transfer mETH from caller to this contract
        require(
            mETH.transferFrom(msg.sender, address(this), mETHAmount),
            "mETH transfer failed"
        );

        // Approve DEX to spend mETH
        mETH.approve(address(dex), mETHAmount);

        // Execute swap
        usdcReceived = dex.swapMETHForUSDC(mETHAmount, minUSDCOut);

        // Verify slippage is within tolerance
        uint256 slippageBps = ((expectedUSDC - usdcReceived) * BASIS_POINTS) /
            expectedUSDC;
        require(slippageBps <= MAX_SLIPPAGE, "Slippage exceeded");

        // Update statistics
        totalSwapsExecuted++;
        totalMETHSwapped += mETHAmount;
        totalUSDCReceived += usdcReceived;

        // Transfer USDC to caller
        require(usdc.transfer(msg.sender, usdcReceived), "USDC transfer failed");

        // Calculate effective rate (USDC per mETH, 6 decimals)
        uint256 effectiveRate = (usdcReceived * 1e18) / mETHAmount;

        emit SwapExecuted(block.timestamp, mETHAmount, usdcReceived, effectiveRate);
    }

    /**
     * @notice Get swap quote from DEX
     * @param mETHAmount Amount of mETH (18 decimals)
     * @return Expected USDC output (6 decimals)
     */
    function getQuote(uint256 mETHAmount) external view returns (uint256) {
        return dex.getQuote(mETHAmount);
    }

    /**
     * @notice Get mETH price from oracle
     * @return Price in USD (18 decimals)
     */
    function getMETHPrice() external view returns (uint256) {
        return priceOracle.getPrice();
    }

    /**
     * @notice Calculate USD value of mETH amount
     * @param mETHAmount Amount of mETH (18 decimals)
     * @return USD value (6 decimals for USDC)
     */
    function getMETHValueUSD(uint256 mETHAmount) external view returns (uint256) {
        uint256 priceUSD = priceOracle.getPrice(); // 18 decimals
        // Convert to USDC 6 decimals: (mETH * price) / 1e18 / 1e12
        return (mETHAmount * priceUSD) / 1e30;
    }

    /**
     * @notice Get swap statistics
     * @return Total swaps, total mETH swapped, total USDC received
     */
    function getSwapStats() external view returns (uint256, uint256, uint256) {
        return (totalSwapsExecuted, totalMETHSwapped, totalUSDCReceived);
    }

    /**
     * @notice Validate swap price against oracle
     * @param mETHAmount mETH amount being swapped
     * @param expectedUSDC Expected USDC from DEX quote
     */
    function _validatePrice(uint256 mETHAmount, uint256 expectedUSDC) internal view {
        uint256 oraclePrice = priceOracle.getPrice(); // USD per mETH (18 decimals)

        // Calculate expected USDC from oracle price
        uint256 oracleExpectedUSDC = (mETHAmount * oraclePrice) / 1e30; // Convert to 6 decimals

        // Allow 5% deviation between oracle and DEX
        uint256 maxDeviation = 500; // 5% in basis points
        uint256 deviationBps;

        if (expectedUSDC > oracleExpectedUSDC) {
            deviationBps =
                ((expectedUSDC - oracleExpectedUSDC) * BASIS_POINTS) /
                oracleExpectedUSDC;
        } else {
            deviationBps =
                ((oracleExpectedUSDC - expectedUSDC) * BASIS_POINTS) /
                oracleExpectedUSDC;
        }

        require(
            deviationBps <= maxDeviation,
            "Price deviation too high"
        );
    }

    /**
     * @notice Emergency withdraw tokens (owner only)
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(
        address token,
        uint256 amount
    ) external onlyOwner {
        require(IERC20(token).transfer(owner(), amount), "Transfer failed");
    }
}

