contracts/core/AttestationRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

contract AttestationRegistry {
    using ECDSA for bytes32;

    struct AssetRecord {
        bytes32 assetId;
        bytes32 attestationHash;
        bytes32 blobId;
        address attestor;
        uint48 timestamp;
        bool revoked;
    }

    mapping(bytes32 => AssetRecord) public assets;
    mapping(address => bool) public trustedAttestors;
    address public owner;

    event AssetRegistered(bytes32 indexed assetId, bytes32 blobId, bytes32 attestationHash, address attestor);
    event AssetRevoked(bytes32 indexed assetId, string reason, uint256 timestamp);

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        trustedAttestors[msg.sender] = true;
    }

    function addTrustedAttestor(address attestor) external onlyOwner {
        trustedAttestors[attestor] = true;
    }

    function registerAsset(
        bytes32 assetId,
        bytes32 attestationHash,
        bytes32 blobId,
        bytes calldata payload,
        bytes calldata signature
    ) external {
        require(assets[assetId].timestamp == 0, "Asset already registered");
        require(keccak256(payload) == attestationHash, "Payload hash mismatch");

        // Recover signer from hash
        bytes32 ethSignedHash = MessageHashUtils.toEthSignedMessageHash(attestationHash);
        address recovered = ECDSA.recover(ethSignedHash, signature);

        require(trustedAttestors[recovered], "Invalid attestor signature");

        assets[assetId] = AssetRecord({
            assetId: assetId,
            attestationHash: attestationHash,
            blobId: blobId,
            attestor: recovered,
            timestamp: uint48(block.timestamp),
            revoked: false
        });

        emit AssetRegistered(assetId, blobId, attestationHash, recovered);
    }

    function revokeAsset(bytes32 assetId, string calldata reason) external onlyOwner {
        require(assets[assetId].timestamp > 0, "Asset not found");
        assets[assetId].revoked = true;
        emit AssetRevoked(assetId, reason, block.timestamp);
    }

    function isAssetValid(bytes32 assetId) external view returns (bool) {
        return assets[assetId].timestamp > 0 && !assets[assetId].revoked;
    }
}

contracts/core/ComplianceModule.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IdentityRegistry.sol";
import "./AttestationRegistry.sol";

contract ComplianceModule {
    IdentityRegistry public identityRegistry;
    AttestationRegistry public attestationRegistry;
    bytes32 public assetId;
    address public owner;

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    constructor(
        address _identityRegistry,
        address _attestationRegistry,
        bytes32 _assetId
    ) {
        owner = msg.sender;
        identityRegistry = IdentityRegistry(_identityRegistry);
        attestationRegistry = AttestationRegistry(_attestationRegistry);
        assetId = _assetId;
    }

    function canTransfer(address from, address to, uint256 /* amount */) external view returns (bool) {
        // 1. Check Sender KYC (unless minting/burning)
        if (from != address(0)) {
            if (!identityRegistry.isVerified(from)) return false;
        }

        // 2. Check Receiver KYC (unless burning)
        if (to != address(0)) {
            if (!identityRegistry.isVerified(to)) return false;
        }

        // 3. Check Asset Validity
        if (!attestationRegistry.isAssetValid(assetId)) return false;

        return true;
    }

    function setIdentityRegistry(address _identityRegistry) external onlyOwner {
        identityRegistry = IdentityRegistry(_identityRegistry);
    }

    function setAttestationRegistry(address _attestationRegistry) external onlyOwner {
        attestationRegistry = AttestationRegistry(_attestationRegistry);
    }
}

contracts/core/IdentityRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./TrustedIssuersRegistry.sol";

contract IdentityRegistry {
    struct Identity {
        bool exists;
        bool isVerified;
        uint256 kycTimestamp;
        address wallet;
    }

    mapping(address => Identity) public identities;
    TrustedIssuersRegistry public trustedIssuersRegistry;
    address public owner;

    event IdentityRegistered(address indexed wallet, uint256 timestamp);
    event IdentityRemoved(address indexed wallet, uint256 timestamp);

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    modifier onlyTrustedIssuer() {
        require(trustedIssuersRegistry.isTrustedIssuer(msg.sender) || msg.sender == owner, "Only trusted issuer");
        _;
    }

    constructor(address _trustedIssuersRegistry) {
        owner = msg.sender;
        trustedIssuersRegistry = TrustedIssuersRegistry(_trustedIssuersRegistry);
    }

    function updateTrustedIssuersRegistry(address _newRegistry) external onlyOwner {
        trustedIssuersRegistry = TrustedIssuersRegistry(_newRegistry);
    }

    function registerIdentity(address wallet) external onlyTrustedIssuer {
        identities[wallet] = Identity({
            exists: true,
            isVerified: true,
            kycTimestamp: block.timestamp,
            wallet: wallet
        });
        emit IdentityRegistered(wallet, block.timestamp);
    }

    function removeIdentity(address wallet) external onlyTrustedIssuer {
        require(identities[wallet].exists, "Identity does not exist");
        identities[wallet].isVerified = false;
        emit IdentityRemoved(wallet, block.timestamp);
    }

    function batchRegisterIdentity(address[] calldata wallets) external onlyTrustedIssuer {
        for (uint256 i = 0; i < wallets.length; i++) {
            identities[wallets[i]] = Identity({
                exists: true,
                isVerified: true,
                kycTimestamp: block.timestamp,
                wallet: wallets[i]
            });
            emit IdentityRegistered(wallets[i], block.timestamp);
        }
    }

    function isVerified(address wallet) external view returns (bool) {
        return identities[wallet].isVerified;
    }

    function getIdentity(address wallet) external view returns (Identity memory) {
        return identities[wallet];
    }
}

